/* ===============================
   2) 상태 / 설정
   =============================== */

let currentLang = "en";
let introLines = INTRO_BY_LANG[currentLang];

const FRAME_INTERVAL_MS = 180;
const INTRO_INTERVAL_MS = 1700;
const WAVE_CYCLES_BEFORE_STOP = 4;
const STOP_AFTER_MS =
  WAVE_FRAMES.length * FRAME_INTERVAL_MS * WAVE_CYCLES_BEFORE_STOP;

const asciiStage = document.getElementById("ascii-stage");
const introLine = document.getElementById("intro-line");
const earthBtn = document.getElementById("earth-btn");
const earthAscii = document.getElementById("earth-ascii");
const languagePanel = document.getElementById("language-panel");

let frameIndex = 0;
let introIndex = 0;

let frameTimer = null;
let introTimeout = null;
let stopTimeout = null;

/* ===============================
   3) 유틸
   =============================== */

function getMaxFrameSize(frames) {
  let maxCols = 0;
  let maxRows = 0;

  for (const frame of frames) {
    const lines = String(frame).replace(/\r/g, "").split("\n");
    if (lines.length > maxRows) maxRows = lines.length;
    for (const line of lines) {
      if (line.length > maxCols) maxCols = line.length;
    }
  }
  return { maxCols, maxRows };
}

function lockAsciiStageSize() {
  const allFrames = [...WAVE_FRAMES, FINAL_FRAME];
  const { maxCols, maxRows } = getMaxFrameSize(allFrames);

  // ✅ 아스키 보존 필수 스타일
  asciiStage.style.whiteSpace = "pre";
  asciiStage.style.fontFamily = "ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, monospace";
  asciiStage.style.lineHeight = "1";
  asciiStage.style.display = "inline-block";
  asciiStage.style.overflow = "hidden";

  // ✅ 줄/문자 기준으로 박스 고정
  asciiStage.style.width = `${maxCols}ch`;
  asciiStage.style.height = `${maxRows}em`;
}

function clearAllTimers() {
  if (frameTimer) clearInterval(frameTimer);
  if (introTimeout) clearTimeout(introTimeout);
  if (stopTimeout) clearTimeout(stopTimeout);
  frameTimer = null;
  introTimeout = null;
  stopTimeout = null;
}

/* ===============================
   4) 렌더 / 애니메이션
   =============================== */

function renderNextFrame() {
  if (!WAVE_FRAMES.length) return;
  asciiStage.textContent = WAVE_FRAMES[frameIndex];
  frameIndex = (frameIndex + 1) % WAVE_FRAMES.length;
}

function playIntroOnce() {
  if (!introLines.length) return;

  introLine.innerHTML = introLines[introIndex];

  if (introIndex >= introLines.length - 1) return;

  introIndex += 1;
  introTimeout = setTimeout(playIntroOnce, INTRO_INTERVAL_MS);
}

function stopAndShowEarth() {
  clearAllTimers();

  // ✅ 빈 FINAL_FRAME 방지됨
  asciiStage.textContent = FINAL_FRAME;
  earthAscii.textContent = ASCII_EARTH;

  earthBtn.classList.remove("hidden");
}

function startAnimation() {
  lockAsciiStageSize();

  // 초기 프레임/문장 먼저 표시
  renderNextFrame();
  playIntroOnce();

  frameTimer = setInterval(renderNextFrame, FRAME_INTERVAL_MS);
  stopTimeout = setTimeout(stopAndShowEarth, STOP_AFTER_MS);
}

function restartAnimationWithLanguage(lang) {
  currentLang = lang;
  introLines = INTRO_BY_LANG[lang] || INTRO_BY_LANG.en;

  if (lang === "ar") introLine.setAttribute("dir", "rtl");
  else introLine.setAttribute("dir", "ltr");

  clearAllTimers();

  frameIndex = 0;
  introIndex = 0;

  languagePanel.classList.add("hidden");
  earthBtn.classList.add("hidden");
  asciiStage.textContent = "";
  introLine.textContent = "";

  startAnimation();
}

function showLanguagePanel() {
  languagePanel.classList.remove("hidden");
  languagePanel.classList.add("open");
}

function hideLanguagePanel() {
  languagePanel.classList.remove("open");
  languagePanel.classList.add("hidden");
}

earthBtn.addEventListener("click", (e) => {
  e.preventDefault();
  e.stopPropagation();

  const isOpen = languagePanel.classList.contains("open");
  if (isOpen) hideLanguagePanel();
  else showLanguagePanel();
});

languagePanel.addEventListener("click", (e) => {
  const langBtn = e.target.closest("[data-lang]");
  if (!langBtn) return;

  e.preventDefault();
  e.stopPropagation();

  const lang = (langBtn.dataset.lang || "").trim().toLowerCase();
  if (!INTRO_BY_LANG[lang]) return;

  hideLanguagePanel();
  restartAnimationWithLanguage(lang);
});


document.addEventListener("click", (e) => {
  const insidePanel = e.target.closest("#language-panel");
  const insideEarth = e.target.closest("#earth-btn");
  if (!insidePanel && !insideEarth) hideLanguagePanel();
});


/* ===============================
   6) 시작
   =============================== */

startAnimation();
